mapper.update:
update users
set last_login = now()
where phone_number = #{phoneNumber}

update users
set password = #{userChangePassword.newPassword}
where id = #{userId}

update users
set password = #{password}
where id = #{userId}

update users
set user_name = #{userModifyInfo.userName},
    avatar    = #{userModifyInfo.avatar}
where id = #{userId}
